member.example:
// Primero registrar contextos
services.AddUnitOfWork<AccountContext>("account");
services.AddUnitOfWork<CatalogContext>("catalog");

// Luego registrar la factory
services.AddUnitOfWorkFactory();

// Uso en servicios:
public MyService(IUnitOfWorkFactory factory)
{
    var accountUoW = factory.GetUnitOfWork("account");
    var catalogUoW = factory.GetUnitOfWork<CatalogContext>();
}
